import React, { useState, useEffect } from 'react';

const LoadingPage = ({ 
  image = "./images/apple airdrop.png", 
  totalSteps = 10 
}) => {
  const [progress, setProgress] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => {
      setProgress((prevProgress) => {
        if (prevProgress < totalSteps) {
          return prevProgress + 1;
        } 
        // Once progress reaches total steps, wait a moment before marking as complete
        if (prevProgress === totalSteps) {
          setTimeout(() => setIsLoading(false), 500);
          return prevProgress;
        }
        return prevProgress;
      });
    }, 500); // Faster interval for smoother progression

    return () => clearInterval(interval);
  }, [totalSteps]);

  if (!isLoading) {
    return null; // Or return your main app component
  }

  return (
    <div className="flex flex-col items-center justify-center h-screen p-4 bg-gray-100">
      <img 
        src={image} 
        alt="Loading" 
        className="max-w-full mb-6 rounded-lg shadow-md"
      />
      <div className="w-full max-w-md bg-gray-300 rounded-full h-2.5">
        <div 
          className="bg-blue-600 h-2.5 rounded-full transition-all duration-500" 
          style={{ width: `${(progress / totalSteps) * 100}%` }}
        ></div>
      </div>
      <p className="mt-3 text-gray-700">
        Loading: {progress} of {totalSteps} steps
      </p>
    </div>
  );
};

export default LoadingPage;
