import React from 'react';
import { Avatar, Badge, List, Cell, Section, Button } from '@telegram-apps/telegram-ui';

export const TasksSection = () => (
  <div style={{
    position: 'relative', // Ensure relative layout for proper stacking
    display: 'flex',
    flexDirection: 'column',
    height: 'calc(100vh - 250px)', // Subtract the tab bar height from the viewport height
    overflow: 'hidden', // Prevent content overflow
  }}>
    {/* Scrollable Area */}
    <div style={{
      flex: 1, // Take up all available vertical space
      overflowY: 'auto', // Enable vertical scrolling
      overflowX: 'hidden', // Prevent horizontal scrolling
    }}>
      <List
        style={{
          background: 'var(--tgui--secondary_bg_color)',
          width: '100%',
          paddingBottom: '16px', // Optional additional padding for aesthetics
        }}
      >
        <Section
          header="Active Games"
          footer="The official Telegram app is available for Android, iPhone, iPad, Windows, macOS and Linux."
        >
          <Cell
            after={<Badge type="number">99</Badge>}
            before={<Avatar size={48} />}
            description="Description"
            subhead="Subhead"
            subtitle="Subtitle"
            titleBadge={<Badge type="dot" />}
          >
            Unique Peak
          </Cell>
          <Cell
            after={<Badge type="number">99</Badge>}
            before={<Avatar size={48} />}
            description="Description"
            subhead="Subhead"
            subtitle="Subtitle"
            titleBadge={<Badge type="dot" />}
          >
            Unique Peak
          </Cell>
          <Cell
            after={<Button mode="filled" size="m">Action </Button>}
            before={<Avatar size={48} />}
            description="Description"
            subhead="Subhead"
            subtitle="Subtitle"
            titleBadge={<Badge type="dot" />}
          >
            Unique Peak
          </Cell>
        </Section>
      </List>
    </div>
  </div>
);

export default TasksSection;
