import React from 'react';
import { Avatar, Badge, List, Cell, Section } from '@telegram-apps/telegram-ui';

export const GameSection = () => (
  <div style={{
    position: 'relative', // Ensure proper layout relative to the parent container
    display: 'flex',
    flexDirection: 'column',
    height: '100vh', // Full viewport height
    overflow: 'hidden', // Prevent content overflow
  }}>
    {/* Scrollable Area */}
    <div style={{
      flex: 1, // Take up all available vertical space
      overflowY: 'auto', // Enable vertical scrolling
      overflowX: 'hidden', // Prevent horizontal scrolling
      marginBottom: '56px', // Prevent scroll area from overlapping the tab bar
    }}>
      <List
        style={{
          background: 'var(--tgui--secondary_bg_color)',
          width: '100%',
        }}
      >
        <Section
          header="Active Games"
          footer="The official Telegram app is available for Android, iPhone, iPad, Windows, macOS and Linux."
        >
          <Cell
            after={<Badge type="number">99</Badge>}
            before={<Avatar size={48} />}
            description="Description"
            subhead="Subhead"
            subtitle="Subtitle"
            titleBadge={<Badge type="dot" />}
          >
            Unique Peak
          </Cell>
          <Cell
            after={<Badge type="number">99</Badge>}
            before={<Avatar size={48} />}
            description="Description"
            subhead="Subhead"
            subtitle="Subtitle"
            titleBadge={<Badge type="dot" />}
          >
            Unique Peak
          </Cell>
          {[...Array(20)].map((_, index) => (
            <Cell key={index}>
              Additional Game {index + 1}
            </Cell>
          ))}
        </Section>
      </List>
    </div>

    {/* Tab Bar */}
    <div style={{
      position: 'fixed',
      bottom: 0,
      width: '100%',
      height: '56px', // Match the height of the tab bar
      backgroundColor: 'var(--tgui--primary_bg_color)',
      zIndex: 1000, // Ensure it appears above the content
    }}>
      {/* Tab bar content (e.g., icons, links) */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-around',
        alignItems: 'center',
        height: '100%',
      }}>
        <div>Tab 1</div>
        <div>Tab 2</div>
        <div>Tab 3</div>
      </div>
    </div>
  </div>
);

export default GameSection;
