import React, { useState } from 'react';
import { TabsList ,Image} from '@telegram-apps/telegram-ui';
import { MyInput } from './MyInput';
import frensImage from '../images/Frens.png';


const TasksSection = () => {
  const [selectedTab, setSelectedTab] = useState('tab1');

  return (
    <div>
      <TabsList>
        <TabsList.Item
          selected={selectedTab === 'tab1'}
          onClick={() => setSelectedTab('tab1')}
        >
          Frens
        </TabsList.Item>
        <TabsList.Item
          selected={selectedTab === 'tab2'}
          onClick={() => setSelectedTab('tab2')}
        >
          Tasks
        </TabsList.Item>

      </TabsList>

      {selectedTab === 'tab1' && (
        <>// Option 1: Using style prop
        <div 
          style={{
            display: 'flex',
            justifyContent: 'center',
            width: '100%',
            padding: '16px'
          }}
        >
          <Image 
            src={frensImage} 
            alt="Frens"
            style={{
              width: '100%',
              maxWidth: '800px',
              height: 'auto'
            }}
          />
        </div>
        
        // Option 2: Or using classic CSS class
        <div className="image-container">
          <Image 
            src={frensImage} 
            alt="Frens"
            className="responsive-image"
          />
        </div>
        
<MyInput/></>
      )}

      {selectedTab === 'tab2' && (
        <div>
          <h2>Tab 2 Content</h2>
          <p>This is the content for Tab 2</p>
        </div>
      )}

    </div>
  );
};

export default TasksSection;
