import React, { useRef } from 'react';
import { Button , Tooltip} from '@telegram-apps/telegram-ui'
import newtonwapple from './newtonwithapple.png'

export default function HowToPlay() {
  const buttonRef = useRef(null);

  return (
    <div 
    style={{
      display: "flex",
      alignItems: "center",
      backgroundColor: 'grey',
      height: "auto", 
      width: "auto", // Changed from 100vw
      gap: 10,
    }}
  >
      <div
        style={{
          flexGrow: 1,
          display: "flex",
          justifyContent: "center",
          width: "90%",
        }}
      >
       



 
    <div>
      <Button
        ref={buttonRef}
        onClick={() => {}}
        style={{
          width: '100px'
        }}
      >
        Hide
      </Button>
      <Tooltip
        mode="light"
        targetRef={buttonRef}
      >
        Hold to record audio. Tap to switch to video.
      </Tooltip>
    </div>
  );
};

export default AudioVideoButton;

      </div>
      
      <img
        src={newtonwapple}
        style={{
          objectFit: "contain",
          width: '160px',
        }}
      />
    </div>
  )
}
