import React from 'react'
import { Button, Avatar, Badge, List, Cell, Section } from '@telegram-apps/telegram-ui';

export default function HowToPlay() {
  return (



<div
            style={{
              display: "flex",
              flexDirection: "column",
              backgroundColor: 'grey',
              height: "80px",
              width: "100vw",
              padding: 20,
              gap: 10,
            }}
          >
            <div
              style={{
                flexGrow: 1,
                display: "flex",
                flexDirection: "column",
                justifyContent: "space-between",
                width: "60%",
              }}
            >
                 <span style={{ fontSize: 20, fontWeight: "bold" }}>
                - Join a game from below
                - Read the NEWTON Laws of each game
                - Submit your guess and get instant reward
                - Come back later to see if you won more rewards! 
              </span>

              <Button
                style={{
                  backgroundColor: "white",
                  color: "black",
                }}
              >
                 <span style={{ fontSize: 20, fontWeight: "bold" }}>
                How to play?
              </span>
              </Button>
            </div>
            
          </div>

)
}
