import React from 'react';
import { Avatar, Badge, List, Cell, Section } from '@telegram-apps/telegram-ui';

export const GameSection = () => (
  <div style={{
    display: 'flex',
    flexDirection: 'column',
    height: 'calc(100vh - 120px)', // Subtract estimated tabbar and header height
    marginTop: '120px', // Push content below fixed header and tabbar
    position: 'absolute',
    left: 0,
    right: 0,
    overflow: 'hidden'
  }}>
    <div style={{
      flex: 1,
      overflowY: 'auto',
      overflowX: 'hidden',
      paddingRight: '10px'
    }}>
      <List
        style={{
          background: 'var(--tgui--secondary_bg_color)',
          width: '100%',
          padding: '0 16px'
        }}
      >
        <Section
          header="Active Games"
          footer="The official Telegram app is available for Android, iPhone, iPad, Windows, macOS and Linux."
        >
          {/* Existing Cells */}
          <Cell 
            after={<Badge type="number">99</Badge>}
            before={<Avatar size={48} />}
            description="Description"
            subhead="Subhead"
            subtitle="Subtitle"
            titleBadge={<Badge type="dot" />}
          >
            Unique Peak
          </Cell>
          <Cell 
            after={<Badge type="number">99</Badge>}
            before={<Avatar size={48} />}
            description="Description"
            subhead="Subhead"
            subtitle="Subtitle"
            titleBadge={<Badge type="dot" />}
          >
            Unique Peak
          </Cell>

          {/* Additional cells to demonstrate scrolling */}
          {[...Array(20)].map((_, index) => (
            <Cell key={index}>
              Additional Game {index + 1}
            </Cell>
          ))}
        </Section>
      </List>
    </div>
  </div>
);

export default GameSection;
