import React, { useState } from 'react';
import { Button , Tooltip} from '@telegram-apps/telegram-ui'
import newtonwapple from './newtonwithapple.png'

export default function HowToPlay() {
  const [showTooltip, setShowTooltip] = useState(false);
  const [buttonText, setButtonText] = useState('Record');

  const handleClick = () => {
    setShowTooltip(!showTooltip);
    setButtonText(showTooltip ? 'Record' : 'Hide');
  };
  return (
    <div 
    style={{
      display: "flex",
      alignItems: "center",
      backgroundColor: 'grey',
      height: "auto", 
      width: "auto", // Changed from 100vw
      gap: 10,
    }}
  >
      <div
        style={{
          flexGrow: 1,
          display: "flex",
          justifyContent: "center",
          width: "90%",
        }}
      >
       
        <div className="relative w-full max-w-xs">
        <button 
          onClick={handleClick}
          className="w-full bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 transition-colors"
        >
          {buttonText}
        </button>
        
        {showTooltip && (
          <div className="absolute z-10 mt-2 p-3 bg-white shadow-lg rounded-lg border border-gray-200 text-sm">
            Hold to record audio. Tap to switch to video.
          </div>
        )}
      </div>


      </div>
      
      <img
        src={newtonwapple}
        style={{
          objectFit: "contain",
          width: '160px',
        }}
      />
    </div>
  )
}
