import React from 'react';
import { Avatar, Badge, Banner, Button, Image, List, Cell, IconContainer, Icon28Chat, Icon28Stats, Icon28Devices, Input, Section } from '@telegram-apps/telegram-ui';

const TON_SITE_LINK = 'https://ton.space/';

export const GameSection = () => (
  <div style={{
    display: 'flex',
    flexDirection: 'column',
    height: '100%', // Full height of parent container
    overflow: 'hidden' // Prevent overall scrolling
  }}>
    <List
      style={{
        background: 'var(--tgui--secondary_bg_color)',
        width: '100%', // Full width
        height: '100%', // Full height
        display: 'flex',
        flexDirection: 'column'
      }}
    >
      <Section
        footer="The official Telegram app is available for Android, iPhone, iPad, Windows, macOS and Linux."
        header="Active Games"
        style={{
          display: 'flex',
          flexDirection: 'column',
          height: '100%', // Full height of parent
          overflow: 'hidden' // Hide overflow
        }}
      >
        {/* Scrollable content area */}
        <div style={{
          flex: 1, // Take remaining space
          overflowY: 'auto', // Enable vertical scrolling
          overflowX: 'hidden', // Prevent horizontal scrolling
          paddingRight: '10px' // Add some padding for scrollbar
        }}>
          <Cell 
            after={<Badge type="number">99</Badge>}
            before={<Avatar size={48} />}
            description="Description"
            subhead="Subhead"
            subtitle="Subtitle"
            titleBadge={<Badge type="dot" />}
          >
            Unique Peak
          </Cell>
          <Cell 
            after={<Badge type="number">99</Badge>}
            before={<Avatar size={48} />}
            description="Description"
            subhead="Subhead"
            subtitle="Subtitle"
            titleBadge={<Badge type="dot" />}
          >
            Unique Peak
          </Cell>
          {/* Add more cells to demonstrate scrolling */}
          {[...Array(10)].map((_, index) => (
            <Cell key={index}>
              Additional Game {index + 1}
            </Cell>
          ))}
        </div>
      </Section>
    </List>
  </div>
);

export default GameSection;
