import React, { useState, useLayoutEffect } from 'react';
import '@telegram-apps/telegram-ui/dist/styles.css';
import { AppRoot, List, Chip, Avatar, Image } from '@telegram-apps/telegram-ui';
import { MyTabbar } from './components/MyTabbar';
import NewtonLogo from '../images/apologonobg.png';
import Loadpic from '../images/apple airdrop.png'; // The loading page image
import LoadingPage from './components/LoadingPage';

// Preload utility function
const preloadImages = (imageUrls) => {
  return Promise.all(
    imageUrls.map(
      (url) =>
        new Promise((resolve, reject) => {
          const img = new Image();
          img.src = url;
          img.onload = resolve;
          img.onerror = reject;
        })
    )
  );
};

export const App = () => {
  const [isReady, setIsReady] = useState(false);

  useLayoutEffect(() => {
    const initializeApp = async () => {
      try {
        // Preload all necessary images
        await preloadImages([NewtonLogo, Loadpic]);

        // Simulate additional data fetching or processing
        await new Promise((resolve) => setTimeout(resolve, 1500)); // Simulate async tasks
      } catch (error) {
        console.error('Error preloading assets:', error);
      } finally {
        setIsReady(true); // Mark the app as ready
      }
    };

    initializeApp();
  }, []);

  if (!isReady) {
    // Directly render the loading page while waiting
    return (
      <AppRoot>
        <LoadingPage imageSrc={Loadpic} />
      </AppRoot>
    );
  }

  return (
    <AppRoot>
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        height: '100vh',
        overflow: 'hidden'
      }}>
        {/* Fixed Header */}
        <List style={{
          background: 'var(--tgui--secondary_bg_color)',
          padding: 10,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          flexShrink: 0
        }}>
          <div style={{
            display: 'flex',
            gap: 25,
            alignItems: 'center'
          }}>
            <Chip mode="elevated" before={<Avatar size={20} />}>
              Ehsan
            </Chip>
            <Image
              size={64}
              src={NewtonLogo}
              alt="Newton Logo"
            />
            <Chip mode="outline" before={<Avatar size={20} />}>
              5000
            </Chip>
          </div>
        </List>

        <MyTabbar />
      </div>
    </AppRoot>
  );
};

export default App;
