import React from 'react';

// Import the necessary styles globally
import '@telegram-apps/telegram-ui/dist/styles.css';

// Import components from the library
import { AppRoot, Cell, Button, List, Section, Chip, Avatar, Image,CompactPagination,CompactPaginationItem } from '@telegram-apps/telegram-ui';
import { MyTabbar } from './components/MyTabbar';
import { useThemeParams } from "@telegram-apps/sdk-react";

// Example data for rendering list cells
const firstName = 'Ehsan';
const numOfTokens = 5000;
const { accentTextColor, bgColor, secondaryBgColor, buttonColor } =
useThemeParams();
export const App = () => (

  <AppRoot>
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      height: '100vh', // Full viewport height
      overflow: 'hidden' // Prevent overall scrolling
    }}>
      {/* Fixed Header */}
      <List style={{
        background: 'var(--tgui--secondary_bg_color)',
        padding: 10,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexShrink: 0 // Prevent shrinking
      }}>
        <div style={{
          display: 'flex',
          gap: 25,
          alignItems: 'center'
        }}>
          <Chip mode="elevated" before={<Avatar size={20} />}>
            {firstName}
          </Chip>
          <Image
            size={48}
            src="https://avatars.githubusercontent.com/u/84640980?v=4" 
            alt="User avatar"
          />
          <Chip mode="outline" before={<Avatar size={20} />}>
            {numOfTokens}
          </Chip>
        </div>
      </List>


      <div
            style={{
              display: "flex",
              flexDirection: "column",
              backgroundColor: 'blue',
              height: "180px",
              width: "100vw",
              padding: 20,
              gap: 10,
            }}
          >
            <div
              style={{
                flexGrow: 1,
                display: "flex",
                flexDirection: "column",
                justifyContent: "space-between",
                width: "60%",
              }}
            >
              <span style={{ fontSize: 30, fontWeight: "bold" }}>
                How to play?
              </span>
              <Button
                style={{
                  backgroundColor: "white",
                  color: "black",
                }}
              >
                How?
              </Button>
            </div>

          </div>


<MyTabbar/>

    </div>
  </AppRoot>
);

export default App;
