    isCssVarsBound() && throwCssVarsBound();

    getCSSVarName ||= (prop) => `--tg-theme-${camelToKebab(prop)}`;

    function forEachEntry(fn: (key: string, value: RGB) => void): void {
      Object.entries(state()).forEach(([k, v]) => {
        v && fn(k, v);
