function rgbBasedOn(signal: Computed<'bg_color' | 'secondary_bg_color' | RGB>) {
  return computed<RGB | undefined>(() => {
    const color = signal();

    return isRGB(color) ? color : color === 'bg_color'
      ? themeBgColor()
      : themeSecondaryBgColor();
  });
}

