import React from 'react';
import { Avatar, Badge, List, Cell, Section, usePlatform } from '@telegram-apps/telegram-ui';

export const GameSection = () => {
  const platform = usePlatform();

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      height: '100%', // Full height of the tab content area
      overflow: 'hidden' // Prevent overall scrolling
    }}>
      <div style={{
        flex: 1, // Take remaining space
        overflowY: 'auto', // Enable vertical scrolling
        overflowX: 'hidden', // Prevent horizontal scrolling
        paddingBottom: platform === 'android' ? '56px' : '0' // Adjust padding for Android
      }}>
        <List
          style={{
            background: 'var(--tgui--secondary_bg_color)',
            width: '100%'
          }}
        >
          <Section
            header="Active Games"
            footer="The official Telegram app is available for Android, iPhone, iPad, Windows, macOS and Linux."
          >
            <Cell 
              after={<Badge type="number">99</Badge>}
              before={<Avatar size={48} />}
              description="Description"
              subhead="Subhead"
              subtitle="Subtitle"
              titleBadge={<Badge type="dot" />}
            >
              Unique Peak
            </Cell>
            <Cell 
              after={<Badge type="number">99</Badge>}
              before={<Avatar size={48} />}
              description="Description"
              subhead="Subhead"
              subtitle="Subtitle"
              titleBadge={<Badge type="dot" />}
            >
              Unique Peak
            </Cell>
            {/* Additional cells to demonstrate scrolling */}
            {[...Array(20)].map((_, index) => (
              <Cell key={index}>
                Additional Game {index + 1}
              </Cell>
            ))}
          </Section>
        </List>
      </div>
    </div>
  );
};

export default GameSection;
