import React from 'react';
import { Button, Avatar, Badge, List, Cell, Section } from '@telegram-apps/telegram-ui';

export const GameSection = () => (
  <div style={{
    position: 'relative', // Ensure relative layout for proper stacking
    display: 'flex',
    flexDirection: 'column',
    height: 'calc(100vh)', // Subtract the tab bar height from the viewport height
    overflow: 'hidden', // Prevent content overflow

  }}>
    {/* Scrollable Area */}
    <div style={{
      flex: 1, // Take up all available vertical space
      overflowY: 'auto', // Enable vertical scrolling
      overflowX: 'hidden', // Prevent horizontal scrolling

 }}>
        <div
            style={{
              display: "flex",
              flexDirection: "column",
              backgroundColor: 'grey',
              height: "180px",
              width: "100vw",
              padding: 20,
              gap: 10,
            }}
          >
            <div
              style={{
                flexGrow: 1,
                display: "flex",
                flexDirection: "column",
                justifyContent: "space-between",
                width: "60%",
              }}
            >
              <span style={{ fontSize: 30, fontWeight: "bold" }}>
                How to play?
              </span>
              <Button
                style={{
                  backgroundColor: "white",
                  color: "black",
                }}
              >
                How?
              </Button>
            </div>
            
          </div>
      <List 
        style={{
          background: 'var(--tgui--secondary_bg_color)',
          width: '100%',
          paddingBottom: '16px', // Optional additional padding for aesthetics
        }}
      >
        <Section
        style={{ margin: '0% 2% '}}
          header="Active Games"
          footer="The official Telegram app is available for Android, iPhone, iPad, Windows, macOS and Linux."
        >
          <Cell
            after={<Badge type="number">99</Badge>}
            before={<Avatar size={48} />}
            description="Description"
            subhead="Subhead"
            subtitle="Subtitle"
            titleBadge={<Badge type="dot" />}
          >
            Unique Peak
          </Cell>
          <Cell
            after={<Badge type="number">99</Badge>}
            before={<Avatar size={48} />}
            description="Description"
            subhead="Subhead"
            subtitle="Subtitle"
            titleBadge={<Badge type="dot" />}
          >
            Unique Peak
          </Cell>
          {/* Additional cells to demonstrate scrolling */}
          {[...Array(20)].map((_, index) => (
            <Cell key={index}>
              Additional Game {index + 1}
            </Cell>
          ))}
        </Section>
      </List>
    </div>
  </div>
);

export default GameSection;
