import React from 'react';

// Import the necessary styles globally
import '@telegram-apps/telegram-ui/dist/styles.css';

// Import components from the library
import { AppRoot, Cell, List, Section, Chip, Avatar, Image } from '@telegram-apps/telegram-ui';
import { MyTabbar } from './components/MyTabbar';

// Example data for rendering list cells
const firstName = 'Ehsan';
const numOfTokens = 5000;

export const App = () => (
  <AppRoot>
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      height: '100vh', // Full viewport height
      overflow: 'hidden', // Prevent overall scrolling

      flexShrink: 0,
      flexWrap: 'wrap', // Allow items to wrap to the next line
      '@media (max-width: 768px)': {
        flexDirection: 'column', // Stack items vertically on smaller screens
        alignItems: 'center' // Center items on the x-axis
      }


    }}>
      {/* Fixed Header */}
      <List style={{
        background: 'var(--tgui--secondary_bg_color)',
        padding: 10,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexShrink: 0 // Prevent shrinking
      }}>
        <div style={{
          display: 'flex',
          gap: 160,
          alignItems: 'center'
        }}>
          <Chip mode="elevated" before={<Avatar size={20} />}>
            {firstName}
          </Chip>
          <Image
            size={96}
            src="https://avatars.githubusercontent.com/u/84640980?v=4" 
            alt="User avatar"
          />
          <Chip mode="outline" before={<Avatar size={20} />}>
            {numOfTokens}
          </Chip>
        </div>
      </List>
<MyTabbar/>

    </div>
  </AppRoot>
);

export default App;
