import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '@telegram-apps/telegram-ui/dist/styles.css';
import { AppRoot, List, Chip, Avatar, Image } from '@telegram-apps/telegram-ui';
import MyTabbar from './components/MyTabbar'; // اطمینان حاصل کنید که این کامپوننت وجود دارد
import NewtonLogo from '../src/images/apologonobg.png'; // مسیر صحیح لوگو
import LoadingPage from './components/LoadingPage'; // اطمینان حاصل کنید که این کامپوننت وجود دارد
import { retrieveLaunchParams } from '@telegram-apps/sdk';

const App = () => {
  const [user, setUser] = useState(null); // اطلاعات کاربر تلگرام
  const [userData, setUserData] = useState(null); // اطلاعات کاربر از بک‌اند
  const [loading, setLoading] = useState(true); // حالت بارگذاری

  useEffect(() => {
    let initDataRaw = '';
    let tg = null;

    // بررسی وجود Telegram WebApp
    if (window.Telegram && window.Telegram.WebApp) {
      tg = window.Telegram.WebApp;
      const launchParams = retrieveLaunchParams();
      initDataRaw = launchParams.initDataRaw || '';
      console.log('در حال اجرا در داخل Telegram WebApp');
    } else {
      // شبیه‌سازی داده‌ها برای محیط توسعه
      console.log('در حال اجرا خارج از Telegram WebApp');
      tg = {
        initDataUnsafe: {
          user: {
            id: 123456789,
            first_name: 'Test',
            last_name: 'User',
          },
        },
        themeParams: {
          secondary_bg_color: '#f0f0f0',
          primary_bg_color: '#ffffff',
        },
        ready: () => {
          console.log('Telegram WebApp is ready (mock)');
        },
      };
      initDataRaw = 'mock_init_data_for_testing'; // مطمئن شوید که این مقدار توسط بک‌اند به درستی شناسایی می‌شود
    }

    // آماده‌سازی وب‌اپ تلگرام
    tg.ready();

    // دریافت اطلاعات کاربر تلگرام یا شبیه‌سازی شده
    const telegramUser = tg.initDataUnsafe?.user || null;
    setUser(telegramUser);
    console.log('اطلاعات کاربر تلگرام:', telegramUser);

    // تابع احراز هویت و دریافت اطلاعات کاربر
    const authenticateAndFetchUser = async () => {
      try {
        console.log('initDataRaw:', initDataRaw);

        // ارسال درخواست POST به /auth/login
        const loginResponse = await axios.post(
          'http://37.27.29.15:3000/auth/login',
          {
            initData: initDataRaw,
            referralToken: '', // در صورت نیاز، مقدار واقعی را جایگزین کنید
          },
          {
            headers: {
              'Content-Type': 'application/json',
              // 'Authorization': `tma ${initDataRaw}`, // اگر بک‌اند به این هدر نیاز دارد، آن را فعال کنید
            },
          }
        );

        console.log('داده‌های پاسخ لاگین:', loginResponse.data);

        const accessToken = loginResponse.data.access_token;

        if (!accessToken) {
          throw new Error('دریافت access_token از پاسخ لاگین موفق نبود');
        }

        console.log('Access Token:', accessToken);

        // ارسال درخواست GET به /user/me
        const userResponse = await axios.get('http://37.27.29.15:3000/user/me', {
          headers: {
            'Authorization': `Bearer ${accessToken}`, // مطمئن شوید که بک‌اند از این فرمت پشتیبانی می‌کند
            // 'Authorization': `tma ${accessToken}`, // اگر بک‌اند از این فرمت استفاده می‌کند، آن را فعال کنید
          },
        });

        console.log('داده‌های پاسخ کاربر:', userResponse.data);

        setUserData(userResponse.data);

        // اضافه کردن تأخیر ۲ ثانیه‌ای قبل از نمایش رابط کاربری
        setTimeout(() => {
          setLoading(false);
          console.log('Loading complete after 2 seconds delay');
        }, 2000);
      } catch (error) {
        if (error.response) {
          // سرور با وضعیت غیر از 2xx پاسخ داده است
          console.error('خطای API:', error.response.status, error.response.data);
        } else if (error.request) {
          // درخواست ارسال شده ولی پاسخی دریافت نشده
          console.error('هیچ پاسخی دریافت نشد:', error.request);
        } else {
          // خطای دیگری رخ داده است
          console.error('خطا:', error.message);
        }
        // در صورت خطا، همچنان بعد از ۲ ثانیه حالت بارگذاری را خاموش می‌کنیم
        setTimeout(() => {
          setLoading(false);
          console.log('Loading complete after 2 seconds delay (error case)');
        }, 2000);
      }
    };

    authenticateAndFetchUser();

    // تنظیم رنگ‌های پوسته
    const { secondary_bg_color, primary_bg_color } = tg.themeParams || {};
    if (secondary_bg_color) {
      document.body.style.backgroundColor = secondary_bg_color;
      console.log('Secondary Background Color Set:', secondary_bg_color);
    }
    if (primary_bg_color) {
      // در صورت نیاز، می‌توانید رنگ‌های دیگر را نیز تنظیم کنید
      console.log('Primary Background Color Set:', primary_bg_color);
    }
  }, []); // اجرای یکبار در زمان mount

  if (loading) {
    return (
      <AppRoot>
        <LoadingPage />
      </AppRoot>
    );
  }

  return (
    <AppRoot>
      <div
        style={{
          display: 'flex',
          flexDirection: 'column',
          height: '100vh', // ارتفاع کامل ویوپورت
          overflow: 'hidden', // جلوگیری از اسکرول کلی
        }}
      >
        {/* هدر ثابت */}
        <List
          style={{
            background: 'var(--tgui--secondary_bg_color)',
            padding: 10,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            flexShrink: 0, // جلوگیری از کوچک شدن
          }}
        >
          <div
            style={{
              display: 'flex',
              gap: 25,
              alignItems: 'center',
              backgroundColor: 'var(--tgui--primary_bg_color)',
              padding: '5px 10px',
              borderRadius: '10px',
            }}
          >
            <Chip mode="elevated" before={<Avatar size={20} />}>
              {user?.first_name || 'Guest'}
            </Chip>
            <Image size={64} src={NewtonLogo} alt="App logo" />
            <Chip mode="outline" before={<Avatar size={20} />}>
              {userData?.tokens ?? 100}
            </Chip>
          </div>
        </List>

        {/* ناوبری تب‌بار */}
        <MyTabbar />
      </div>
    </AppRoot>
  );
};

export default App;
