import { Avatar, Badge, Banner, Button, Image,List, Cell, IconContainer, Icon28Chat,Icon28Stats,Icon28Devices,Input, Section } from '@telegram-apps/telegram-ui';

const TON_SITE_LINK = 'https://ton.space/';

export const GameSection = () => (
<>
 {/* Scrollable Content Area */}
 <div style={{
      flex: 1, // Take remaining space
      overflowY: 'auto', // Enable vertical scrolling
      overflowX: 'hidden' // Prevent horizontal scrolling
    }}>
      {/* Your scrollable content goes here */}
      <div style={{ padding: 16 }}>
        {/* Example content */}

        <List
          style={{
            background: 'var(--tgui--secondary_bg_color)',
            padding: '40px',
            width: 500
          }}
        >

  <Section
    footer="The official Telegram app is available for Android, iPhone, iPad, Windows, macOS and Linux."
    header="Active Games">
      
    <Cell 
     after={<Badge type="number">99</Badge>}
     before={<Avatar size={48} />}
     description="Description"
     subhead="Subhead"
     subtitle="Subtitle"
     titleBadge={<Badge type="dot" />}>
      Unique Peak
    </Cell>
    <Cell 
     after={<Badge type="number">99</Badge>}
     before={<Avatar size={48} />}
     description="Description"
     subhead="Subhead"
     subtitle="Subtitle"
     titleBadge={<Badge type="dot" />}>
      Unique Peak
    </Cell>
    <Cell>
      Devices
    </Cell>
  </Section>
</List>


      </div>
    </div>









</>
  
);
