import React, { useState } from 'react';
import { TabsList ,Image,Button,Placeholder} from '@telegram-apps/telegram-ui';
import { MyInput } from './MyInput';
import frensImage from '../images/Frens.png';


const TasksSection = () => {
  const [selectedTab, setSelectedTab] = useState('tab1');

  return (
    
    <div>
      <TabsList>
        <TabsList.Item
          selected={selectedTab === 'tab1'}
          onClick={() => setSelectedTab('tab1')}
        >
          Frens
        </TabsList.Item>
        <TabsList.Item
          selected={selectedTab === 'tab2'}
          onClick={() => setSelectedTab('tab2')}
        >
          Tasks
        </TabsList.Item>

      </TabsList>

      {selectedTab === 'tab1' && (
        <>
          {/* Scrollable Area */}
    <div style={{
      flex: 1, // Take up all available vertical space
      overflowY: 'auto', // Enable vertical scrolling
      overflowX: 'hidden', // Prevent horizontal scrolling
    }}>
        <div
            style={{
              display: "flex",
              flexDirection: "column",
              height: "100vh",
              width: "100vw",
              alignItems: "center"
            }}
          >
    
        <div>
  <Placeholder
    action={<Button size="l" stretched>Action</Button>}
    description="Its Fun when Frens are around! Invite a fren and you both get 1000 E bonus!"
    header="You have 5 Frens"
  >
    <img
      alt="Telegram sticker"
      src={frensImage}
      style={{
        maxWidth: "100%",
        height: "auto",
        objectFit: "contain",
      }}

    />
  </Placeholder>
</div>

<MyInput/></div></div></>
      )}

      {selectedTab === 'tab2' && (
        <div>
          <h2>Tab 2 Content</h2>
          <p>This is the content for Tab 2</p>
        </div>
      )}

    </div>
  );
};

export default TasksSection;
