import React, { useState, useLayoutEffect } from 'react';
// Import the necessary styles globally
import '@telegram-apps/telegram-ui/dist/styles.css';

// Import components from the library
import { AppRoot, List, Chip, Avatar, Image } from '@telegram-apps/telegram-ui';
import { MyTabbar } from './components/MyTabbar';
import NewtonLogo from '../src/images/apologonobg.png';
import LoadingPage from './components/LoadingPage';

export const App = () => {
  const [isLoading, setIsLoading] = useState(true);

  useLayoutEffect(() => {
    const loadAppData = async () => {
      try {
        // Load all required assets or data here
        const fakeDataLoad = new Promise(resolve => setTimeout(resolve, 2000));

        await fakeDataLoad; // Simulate an async data-fetching process
        // Add any additional initialization logic if needed
      } catch (error) {
        console.error("Error during app initialization", error);
      } finally {
        setIsLoading(false);
      }
    };

    loadAppData();
  }, []);

  if (isLoading) {
    return (
      <AppRoot>
        <LoadingPage />  
      </AppRoot>
    );
  }

  return (
    <AppRoot>
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        height: '100vh', // Full viewport height
        overflow: 'hidden' // Prevent overall scrolling
      }}>
        {/* Fixed Header */}
        <List style={{
          background: 'var(--tgui--secondary_bg_color)',
          padding: 10,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          flexShrink: 0 // Prevent shrinking
        }}>
          <div style={{
            display: 'flex',
            gap: 25,
            alignItems: 'center'
          }}>
            <Chip mode="elevated" before={<Avatar size={20} />}>
              Ehsan
            </Chip>
            <Image
              size={64}
              src={NewtonLogo}
              alt="Newton Logo"
            />
            <Chip mode="outline" before={<Avatar size={20} />}>
              5000
            </Chip>
          </div>
        </List>

        <MyTabbar />
      </div>
    </AppRoot>
  );
};

export default App;
