import React, { useState } from 'react';
import { TabsList, Image, Button, Placeholder } from '@telegram-apps/telegram-ui';
import { MyInput } from './MyInput';
import frensImage from '../images/Frens.png';

const TasksSection = () => {
  const [selectedTab, setSelectedTab] = useState('tab1');

  return (
    <div>
      {/* Wrap TabsList with margin */}
      <div style={{ 
        marginHorizontal: 20,
        marginTop: 10 // Optional: adds some top spacing
      }}>
        <TabsList>
          <TabsList.Item
            selected={selectedTab === 'tab1'}
            onClick={() => setSelectedTab('tab1')}
          >
            Frens
          </TabsList.Item>
          <TabsList.Item
            selected={selectedTab === 'tab2'}
            onClick={() => setSelectedTab('tab2')}
          >
            Tasks
          </TabsList.Item>
        </TabsList>
      </div>

      {/* Scrollable content with consistent margins */}
      <div 
        style={{
          height: 'calc(100vh - 100px)', // Adjusted height
          overflowY: 'auto',
          overflowX: 'hidden',
          paddingHorizontal: 20, // Add horizontal padding to maintain margins
        }}
      >
        {selectedTab === 'tab1' && (
          <div 
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            <div style={{ width: '100%' }}>
              <Placeholder
                action={<Button size="l" stretched>Action</Button>}
                description="Its Fun when Frens are around! Invite a fren and you both get 1000 E bonus!"
                header="You have 5 Frens"
                className="w-full h-auto"
              >
                <img
                  alt="Telegram sticker"
                  src={frensImage}
                  style={{
                    maxWidth: "100%",
                    height: "auto",
                    objectFit: "contain",
                  }}
                />
              </Placeholder>
            </div>
            <MyInput/>
          </div>
        )}

        {selectedTab === 'tab2' && (
          <div>
            <h2>Tab 2 Content</h2>
            <p>This is the content for Tab 2</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default TasksSection;
