import React from 'react'
import { Button , Tooltip} from '@telegram-apps/telegram-ui'
import newtonwapple from './newtonwithapple.png'

export default function HowToPlay() {
  return (
    <div 
    style={{
      display: "flex",
      alignItems: "center",
      backgroundColor: 'grey',
      height: "auto", 
      width: "auto", // Changed from 100vw
      gap: 10,
    }}
  >
      <div
        style={{
          flexGrow: 1,
          display: "flex",
          justifyContent: "center",
          width: "90%",
        }}
      >
       
      <Button
        ref={{
          current: '[Circular]'
        }}
        onClick={function noRefCheck(){}}
        style={{
          width: 100
        }}
      >
        Hide
      </Button>
      <Tooltip
        mode="light"
        targetRef={{
          current: '[Circular]'
        }}
      >
        Hold to record audio. Tap to switch to video.
      </Tooltip>

      </div>
      
      <img
        src={newtonwapple}
        style={{
          objectFit: "contain",
          width: '160px',
        }}
      />
    </div>
  )
}
