import React, { useState } from 'react';
import { TabsList, TabsItem } from '@telegram-apps/telegram-ui';

const TasksSection = () => {
  // State to manage the selected tab
  const [selectedTab, setSelectedTab] = useState('tab1');

  // Components for each tab
  const tabComponents = {
    tab1: (
      <div className="p-4 bg-blue-50">
        <h2 className="text-xl font-bold">First Tab Content</h2>
        <p>This is the detailed content for the first tab.</p>
      </div>
    ),
    tab2: (
      <div className="p-4 bg-green-50">
        <h2 className="text-xl font-bold">Second Tab Content</h2>
        <p>Here's what's in the second tab.</p>
      </div>
    ),
    tab3: (
      <div className="p-4 bg-purple-50">
        <h2 className="text-xl font-bold">Third Tab Content</h2>
        <p>Content for the third tab goes here.</p>
      </div>
    )
  };

  return (
    <div>
      <TabsList>
        <TabsList.Item
          selected={selectedTab === 'tab1'}
          onClick={() => setSelectedTab('tab1')}
        >
          Label 1
        </TabsList.Item>
        <TabsList.Item
          selected={selectedTab === 'tab2'}
          onClick={() => setSelectedTab('tab2')}
        >
          Label 2
        </TabsList.Item>
        <TabsList.Item
          selected={selectedTab === 'tab3'}
          onClick={() => setSelectedTab('tab3')}
        >
          Label 3
        </TabsList.Item>
      </TabsList>

      {/* Render the content for the selected tab */}
      <div className="mt-4">
        {tabComponents[selectedTab]}
      </div>
    </div>
  );
};

export default TasksSection;
