import React, { useState } from 'react';
import { Home, User, Settings } from 'lucide-react';

const HomeComponent = () => <div>Home Content</div>;
const ProfileComponent = () => <div>Profile Content</div>;
const SettingsComponent = () => <div>Settings Content</div>;

export const TabbarComponent = () => {
  const tabs = [
    { 
      id: 'home', 
      text: 'Home', 
      Icon: Home,
      Component: HomeComponent
    },
    { 
      id: 'profile', 
      text: 'Profile', 
      Icon: User,
      Component: ProfileComponent
    },
    { 
      id: 'settings', 
      text: 'Settings', 
      Icon: Settings,
      Component: SettingsComponent
    }
  ];

  const [currentTab, setCurrentTab] = useState(tabs[0].id);
  const CurrentComponent = tabs.find(tab => tab.id === currentTab).Component;

  return (
    <div>
      <div className="fixed bottom-0 left-0 right-0 flex justify-around bg-white shadow-md p-2">
        {tabs.map(({ id, text, Icon }) => (
          <button 
            key={id}
            className={`flex flex-col items-center justify-center w-full py-2 ${
              currentTab === id 
                ? 'text-blue-600 font-bold' 
                : 'text-gray-500 hover:text-gray-700'
            }`}
            onClick={() => setCurrentTab(id)}
          >
            <Icon className="mb-1" size={24} />
            <span className="text-xs">{text}</span>
          </button>
        ))}
      </div>
      
      <div className="pt-16">
        <CurrentComponent />
      </div>
    </div>
  );
};

export default MyTabbar;
