import newtonwapple from './newtonwithapple.png'
import { useRef, useState } from 'react';
//import styles from './TooltipSection.module.css';

import { Banner, Button, Section, Tooltip } from '@telegram-apps/telegram-ui';
export default function HowToPlay() {

  const ref = useRef(null);
  const [shown, setShown] = useState(false);

  return (
    <div 
    style={{
      display: "flex",
      alignItems: "center",
      backgroundColor: 'grey',
      height: "auto", 
      width: "auto", // Changed from 100vw
      gap: 10,
    }}
  >
      <div
        style={{
          flexGrow: 1,
          display: "flex",
          justifyContent: "center",
          width: "10%",
        }}
      >
    
        <Button
          ref={ref}
          size="l"
          width= "70%"
          style={{minWidth:'64px'}}
          onClick={() => setShown(!shown)}
        >
          {shown ? 'Okay!' : 'How to Play?'}
        </Button>
     
      {shown && (
        <Tooltip
          mode="dark"
          targetRef={ref}
        >
          Look in the mirror, you look so cute! Xx
        </Tooltip>
      )}


      </div>
      
      <img
        src={newtonwapple}
        style={{
          objectFit: "contain",
          width: '160px',
        }}
      />
    </div>
  )
}
