import React, { useState } from 'react';
import { TabsList, Image, Button, Placeholder } from '@telegram-apps/telegram-ui';
import { MyInput } from './MyInput';
import frensImage from '../images/Frens.png';

const TasksSection = () => {
  const [selectedTab, setSelectedTab] = useState('tab1');

  return (
    <div style={{ margin: '0% 4% '}}>
      {/* Add margin directly to the TabsList */}
      <TabsList> 
        <TabsList.Item 
          selected={selectedTab === 'tab1'}
          onClick={() => setSelectedTab('tab1')}
        >
          Frens
        </TabsList.Item>
        <TabsList.Item
          selected={selectedTab === 'tab2'}
          onClick={() => setSelectedTab('tab2')}
        >
          Tasks
        </TabsList.Item>
      </TabsList>

      <div 
        style={{
          height: 'calc(100vh - 50px)', 
          overflowY: 'auto', 
          overflowX: 'hidden', 
        }}
      >
        {selectedTab === 'tab1' && (
          <div 
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              padding: '0 20px'
            }}
          >
            <div>
              <Placeholder
                action={<Button size="l" stretched>Action</Button>}
                description="Its Fun when Frens are around! Invite a fren and you both get 1000 E bonus!"
                header="You have 5 Frens"
                className="w-full h-auto"
              >
                <img
                  alt="Telegram sticker"
                  src={frensImage}
                  style={{
                    maxWidth: "100%",
                    height: "auto",
                    objectFit: "contain",
                  }}
                />
              </Placeholder>
            </div>
            <MyInput/>
          </div>
        )}

        {selectedTab === 'tab2' && (
          <div style={{ padding: '0 20px' }}>
            <h2>Tab 2 Content</h2>
            <p>This is the content for Tab 2</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default TasksSection;
