import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Import the necessary styles globally
import { WebApp } from '@telegram-apps/sdk-react';

import '@telegram-apps/telegram-ui/dist/styles.css';

// Import components from the library
import { AppRoot, Cell, Button, List, Section, Chip, Avatar, Image, CompactPagination, CompactPaginationItem } from '@telegram-apps/telegram-ui';
import { MyTabbar } from './components/MyTabbar';
import { useThemeParams } from "@telegram-apps/sdk-react";
import NewtonLogo from '../src/images/apologonobg.png'
import LoadingPage from './components/LoadingPage';

// Example data for rendering list cells
const firstName = 'Ehsan';
const numOfTokens = 5000;

export const App = () => {
    const [isLoading, setIsLoading] = useState(true);
    const [userData, setUserData] = useState(null);
    const [authError, setAuthError] = useState(null);
  
    useEffect(() => {
      // Ensure Telegram WebApp is initialized
      if (!window.Telegram || !window.Telegram.WebApp) {
        console.error('Telegram WebApp SDK not loaded');
        return;
      }
  
      // Initialize the WebApp
      const webapp = window.Telegram.WebApp;
      webapp.ready();
  
      // Get user data from Telegram
      const initDataUnsafe = webapp.initDataUnsafe;
      
      // Check if user is authenticated
      if (!initDataUnsafe || !initDataUnsafe.user) {
        setAuthError('Unable to authenticate');
        setIsLoading(false);
        return;
      }
  
      // Prepare authorization headers
      const authHeaders = {
        'Telegram-Init-Data': webapp.initData,
        'Content-Type': 'application/json'
      };
  
      // Fetch user data from your backend
      axios.get("http://37.27.29.15:3000/user/me", {
        headers: authHeaders
      })
      .then((response) => {
        setUserData(response.data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error("There was a problem fetching the data:", error);
        setAuthError('Failed to fetch user data');
        setIsLoading(false);
      });
    }, []);
  
    // Handle loading and error states
    if (authError) {
      return (
        <AppRoot>
          <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center', 
            height: '100vh' 
          }}>
            <p>{authError}</p>
          </div>
        </AppRoot>
      );
    }
  

  return (
    <AppRoot>
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        height: '100vh', // Full viewport height
        overflow: 'hidden' // Prevent overall scrolling
      }}>
        {/* Fixed Header */}
        <List style={{
          background: 'var(--tgui--secondary_bg_color)',
          padding: 10,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          flexShrink: 0 // Prevent shrinking
        }}>
          <div style={{
            display: 'flex',
            gap: 25,
            alignItems: 'center'
          }}>
            <Chip mode="elevated" before={<Avatar size={20} />}>
              {userData.firstName}
            </Chip>
            <Image
              size={64}
              src={NewtonLogo}
              alt="User avatar"
            />
            <Chip mode="outline" before={<Avatar size={20} />}>
              {numOfTokens}
            </Chip>
          </div>
        </List>
        
        <MyTabbar/>
      </div>
    </AppRoot>
  );
};

export default App;
