import React, { useState } from 'react';
import { Button , Tooltip} from '@telegram-apps/telegram-ui'
import newtonwapple from './newtonwithapple.png'

export default function HowToPlay() {
  const [showTooltip, setShowTooltip] = useState(false);
  const [buttonText, setButtonText] = useState('Record');

  const handleClick = () => {
    setShowTooltip(!showTooltip);
    setButtonText(showTooltip ? 'Record' : 'Hide');
  };
  return (
    <div 
    style={{
      display: "flex",
      alignItems: "center",
      backgroundColor: 'grey',
      height: "auto", 
      width: "auto", // Changed from 100vw
      gap: 10,
    }}
  >
      <div
        style={{
          flexGrow: 1,
          display: "flex",
          justifyContent: "center",
          width: "90%",
        }}
      >
       
       const ref = useRef(null);
  const [shown, setShown] = useState(false);

  return (
    <Section header="Tooltip section">
      <Banner
        header="Tooltip on button"
        subheader="Press the button to show the tooltip"
      >
        <Button
          ref={ref}
          size="s"
          className={styles.button}
          onClick={() => setShown(!shown)}
        >
          {shown ? 'Hide' : 'Show'}
        </Button>
      </Banner>
      {shown && (
        <Tooltip
          mode="dark"
          targetRef={ref}
        >
          Look in the mirror, you look so cute! Xx
        </Tooltip>
      )}
    </Section>


      </div>
      
      <img
        src={newtonwapple}
        style={{
          objectFit: "contain",
          width: '160px',
        }}
      />
    </div>
  )
}
