import React, { useState } from 'react';
import { TabsList } from '@telegram-apps/telegram-ui';

const TasksSection = () => {
  const [selectedTab, setSelectedTab] = useState('tab1');

  return (
    <div style={{ width: '100%' }}>
      <TabsList>
        <TabsList.Item
          selected={selectedTab === 'tab1'}
          onClick={() => setSelectedTab('tab1')}
        >
          Label 1
        </TabsList.Item>
        <TabsList.Item
          selected={selectedTab === 'tab2'}
          onClick={() => setSelectedTab('tab2')}
        >
          Label 2
        </TabsList.Item>
        <TabsList.Item
          selected={selectedTab === 'tab3'}
          onClick={() => setSelectedTab('tab3')}
        >
          Label 3
        </TabsList.Item>
      </TabsList>

      <div style={{ 
        padding: '10px', 
        border: '1px solid #ccc', 
        marginTop: '10px' 
      }}>
        {selectedTab === 'tab1' && (
          <div>
            <h2>Tab 1 Content</h2>
            <p>Detailed content for Tab 1</p>
          </div>
        )}

        {selectedTab === 'tab2' && (
          <div>
            <h2>Tab 2 Content</h2>
            <p>Detailed content for Tab 2</p>
          </div>
        )}

        {selectedTab === 'tab3' && (
          <div>
            <h2>Tab 3 Content</h2>
            <p>Detailed content for Tab 3</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default TasksSection;
